// data because of NO server
const data = {
  "currentUser": {
    "image": { 
      "png": "./images/avatars/image-juliusomo.png",
      "webp": "./images/avatars/image-juliusomo.webp"
    },
    "username": "juliusomo"
  },
  "comments": [
    {
      "id": 1,
      "content": "Impressive! Though it seems the drag feature could be improved. But overall it looks incredible. You've nailed the design and the responsiveness at various breakpoints works really well.",
      "createdAt": "1 month ago",
      "score": 12,
      "user": {
        "image": { 
          "png": "./images/avatars/image-amyrobson.png",
          "webp": "./images/avatars/image-amyrobson.webp"
        },
        "username": "amyrobson"
      },
      "replies": []
    },
    {
      "id": 2,
      "content": "Woah, your project looks awesome! How long have you been coding for? I'm still new, but think I want to dive into React as well soon. Perhaps you can give me an insight on where I can learn React? Thanks!",
      "createdAt": "2 weeks ago",
      "score": 5,
      "user": {
        "image": { 
          "png": "./images/avatars/image-maxblagun.png",
          "webp": "./images/avatars/image-maxblagun.webp"
        },
        "username": "maxblagun"
      },
      "replies": [
        {
          "id": 3,
          "content": "If you're still new, I'd recommend focusing on the fundamentals of HTML, CSS, and JS before considering React. It's very tempting to jump ahead but lay a solid foundation first.",
          "createdAt": "1 week ago",
          "score": 4,
          "replyingTo": "maxblagun",
          "user": {
            "image": { 
              "png": "./images/avatars/image-ramsesmiron.png",
              "webp": "./images/avatars/image-ramsesmiron.webp"
            },
            "username": "ramsesmiron"
          }
        },
        {
          "id": 4,
          "content": "I couldn't agree more with this. Everything moves so fast and it always seems like everyone knows the newest library/framework. But the fundamentals are what stay constant.",
          "createdAt": "2 days ago",
          "score": 2,
          "replyingTo": "ramsesmiron",
          "user": {
            "image": { 
              "png": "./images/avatars/image-juliusomo.png",
              "webp": "./images/avatars/image-juliusomo.webp"
            },
            "username": "juliusomo"
          }
        }
      ]
    }
  ]
};
const {comments} = data;
const {} = data; 
console.log(data);
comments.forEach ((element) => createReplyElement(element));
// function fn1(element){
	
// console.log(element);
// console.log(element.user.image.png);
// console.log(element.user.username);
// console.log(element.score);
// console.log(element.content);
// }

// Body of js 
const blockComment = document.querySelector('.comment');
const blockReply = document.querySelector('.reply');

const replyInput = document.querySelector('.reply__form__input');
const replySubmit = document.getElementById('form');

const clickReplyForReply = document.querySelector('.replyto__comment__body__title__reply__all');
const clickReply = document.querySelector('.comment__body__title__reply__all');

console.log(blockComment);

clickReply.addEventListener('click', enableReply);
replySubmit.addEventListener('submit', createReplyEvent);
clickReplyForReply.addEventListener('click', enableReply);


function disebleReply(){
	blockReply.classList.remove('activeflex');
}
function enableReply(){
	blockReply.classList.add('activeflex');
	clickReply.removeEventListener('click', enableReply);
}
function createReplyElement(element){
	
	blockComment.insertAdjacentHTML('afterend', `
		<div class="replyto">
	      	<div class="replyto__vl">
	      	</div>
	      	<div class="replyto__comment">
		        <div class="replyto__comment__points">
			        <img class="replyto__comment__points__img" src="images/icon-plus.svg" alt="minus"></img>
			        <span class="replyto__comment__points__point">${element.score}</span>
			        <img class='replyto__comment__points__img' src='images/icon-minus.svg' alt="minus"></img>
		    	</div>
	        	<div class="replyto__comment__body">
	          		<div class="replyto__comment__body__title">
			            <img class="replyto__comment__body__title__img" src="${element.user.image.png}" alt="amy">
			            <span class="replyto__comment__body__title__name">${element.user.username}</span>
			            <span class="replyto__comment__body__title__time">${element.createdAt}</span>
			            <div class="replyto__comment__body__title__reply__all">
				            <img src="images/icon-reply.svg" alt="reply">
				            <span class="replyto__comment__body__title__reply">Reply</span>
	            		</div>  
	          		</div>
	          		<p class="replyto__comment__body__text">${element.content}</p>
	        	</div>
	      	</div> 
    	</div> 
		`);
	disebleReply();
}
function createReplyEvent(event){
	event.preventDefault();
	blockComment.insertAdjacentHTML('afterend', `
		<div class="replyto">
	      	<div class="replyto__vl">
	      	</div>
	      	<div class="replyto__comment">
		        <div class="replyto__comment__points">
			        <img class="replyto__comment__points__img" src="images/icon-plus.svg" alt="minus"></img>
			        <span class="replyto__comment__points__point">0</span>
			        <img class='replyto__comment__points__img' src='images/icon-minus.svg' alt="minus"></img>
		    	</div>
	        	<div class="replyto__comment__body">
	          		<div class="replyto__comment__body__title">
			            <img class="replyto__comment__body__title__img" src="${data.currentUser.png}" alt="amy">
			            <span class="replyto__comment__body__title__name">${data.currentUser.username}</span>
			            <span class="replyto__comment__body__title__time">Now</span>
			            <div class="replyto__comment__body__title__reply__all">
				            <img src="images/icon-reply.svg" alt="reply">
				            <span class="replyto__comment__body__title__reply">Reply</span>
	            		</div>  
	          		</div>
	          		<p class="replyto__comment__body__text">${replyInput.value}</p>
	        	</div>
	      	</div> 
    	</div> 
		`);
	disebleReply();	
	};
