// colors
$mblue: hsl(238, 40%, 52%);
$sred: hsl(358, 79%, 66%);
$lblue: hsl(239, 57%, 85%);
$pred: hsl(357, 100%, 86%);
// backgroundcolors
$dblue: hsl(212, 24%, 26%);
$gblue: hsl(211, 10%, 45%);
$lgrey: hsl(223, 19%, 93%);
$vlgrey: hsl(228, 33%, 97%);
$white: hsl(0, 0%, 100%);

sources.helpers\\_mixin.scss:
@mixin points($color:$mblue){
	font-size: 16px;
	color: $color;
	font-family: rubik;
	font-weight: 700;
}

sources.helpers\\_replyform.scss:
@import '_vars';
@import '_mixin';
.reply{
	margin-top: 20px;
	margin-bottom: 20px;
	display: flex;
	background-color: $white;
	border-radius: 5px;
	&__img{
		margin: 20px 0 20px 20px;
		width: 40px;
		height: 40px;
	}
	&__form{
		margin-left: 20px;
		margin-top: 20px;

		&__input{
			border:1px solid $mblue;
			border-radius: 5px;
			width: 600px;
			height: 100px;
			resize: vertical;
			margin-bottom: 20px;
		}
		&__btn{
			width: 80px;
			height: 35px;
			color: $white;
			background-color: $mblue;
			border-radius: 5px;
			margin-left: 20px;
			border: none;
			&:hover{
			cursor: pointer;
			background-color: $lblue;

			}
		}	
		&__all{
			display: flex;
			align-items: flex-start;
		}
	}
}

